    public function candidateFilter(Request $request)
    {
      // dd($request->active_case);
      $filled =$request->active_case;
      $business_id = Auth::user()->business_id;
      $customers = DB::table('users as u')
                    ->select('u.id','u.name','u.first_name','u.last_name','u.email','u.phone','b.company_name')
                    ->join('user_businesses as b','b.business_id','=','u.id')
                    ->where(['u.user_type'=>'client','u.parent_id'=>$business_id])
                    ->get();
      $services = DB::table('services')
                ->select('name','id')
                ->where(['status'=>'1'])
                ->where('business_id',NULL)
                ->whereNotIn('type_name',['e_court'])
                ->orwhere('business_id',$business_id)
                ->orderBy('sort_number','asc')
                ->get();

      // $candidates = DB::table('candidate_reinitiates')
      //         ->select('id','display_id','first_name','middle_name','last_name','phone','name')
      //         ->where(['parent_id'=>$business_id,'user_type'=>'candidate'])
      //         ->orderBy('id','DESC')
      //         ->get();    

    
      $array_result = [];

      foreach ($services as $key => $value) {
          
          
          $insuf = DB::table('jaf_form_data as jf')
                  ->select('jf.id')
                  ->join('candidate_reinitiates as u','u.id','=','jf.candidate_id')
                  ->where(['jf.service_id'=> $value->id,'u.parent_id'=>Auth::user()->business_id,'jf.is_insufficiency'=>'1'])
                  ->count();

          $array_result[] = ['check_id'=>$value->id,'check_name'=> $value->name,'insuf'=>$insuf]; 
             
      }

      $viewRender = view('admin.candidates.filter',compact('customers','array_result','services','filled'))->render();
              return response()->json(array('success' => true, 'html'=>$viewRender));
    }

    public function candidateFilterReferenceNumber(Request $request)
    {
        $business_id = Auth::user()->business_id;
        $candidates = DB::table('candidate_reinitiates')
            ->select('id','display_id','first_name','middle_name','last_name','phone','name')
            ->where(['parent_id'=>$business_id,'user_type'=>'candidate','status'=>1,'is_deleted'=>0])
            ->orderBy('id','DESC')
            ->get(); 
            
        $viewRender = view('admin.candidates.filterreferencenumber',compact('candidates'))->render();
              return response()->json(array('success' => true, 'data'=>$viewRender));
        
                
    }
