    // call data save

    public function callingDataSave(Request $request)
    {
      //dd($request->all());
      $business_id = Auth::user()->business_id;
      DB::beginTransaction();
      try
      {
          $service = [];
          $check = $request->input('check');
          $service = implode(',', $check); 
          // save user data
          $calls_data    = [
            'business_id'   => $business_id,
            'candidate_id'    => $candidate_id,
            'person_name'    => $request->input('person_name'),
            'remarks'        => $request->input('remarks'),
            'start_date'     => date('Y-m-d H:i:s',strtotime($request->input('start_date'))),
            'end_date'       => date('Y-m-d H:i:s',strtotime($request->input('end_date'))), 
            'created_by'     => Auth::user()->id,
            'service_id'    => $service,
          
          ];
        
          $candidateId = DB::table('candidate_calls')->insertGetId($calls_data);

          $allowedextension=['jpg','jpeg','png','svg','pdf','JPG','PDF','JPEG','PNG'];
          if($request->hasFile('attachment') && $request->file('attachment') !="")
          {
                $filePath = public_path('/uploads/calls/'); 
                $files= $request->file('attachment');

                foreach($files as $file)
                {
                    $extension = $file->getClientOriginalExtension();

                    $check = in_array($extension,$allowedextension);

                    if(!$check)
                    {
                        return response()->json([
                          'fail' => true,
                          'errors' => ['attachment' => 'Only jpg,jpeg,png,pdf are allowed !'],
                          'error_type'=>'validation'
                        ]);                        
                    }
                }

                foreach($files as $file){
                    $file_data = $file->getClientOriginalName();
                    $file_ext = $file->getClientOriginalExtension();
                    $tmp_data  = $candidate_id.'-'.date('mdYHis').'.'.$file_ext; 
                    $data = $file->move($filePath, $tmp_data);       
                    $attach_on_select[]=$tmp_data;
                    $path=public_path()."/uploads/calls/".$tmp_data; 
                    
                    $candiadteAttachment = ([
                      'call_id'     => $candidateId,
                      'business_id'  => $business_id,
                      'candidate_id' => $candidate_id,
                      'file_name' => $tmp_data,
                      'created_by'   =>  Auth::user()->id,
                    ]);
                    DB::table('candidate_calls_attachments')->insert($candiadteAttachment);
                } 
            }
          DB::commit();
          return response()->json([
            'success' =>true,
            'custom'  =>'yes',
            'errors'  =>[]
          ]);
        }
        catch (\Exception $e) {
            DB::rollback();
            // something went wrong
            return $e;
      }   
    }

