                  if($is_updated){
                    //generate report here 
                    $this->generateCandidateReport($candidate_id);

                    // Update the clear Status for Insuff Data

                    CandidateInsuffData::where('candidate_id',$candidate_id)->update([
                      'status' => 1
                    ]);

                    DB::commit();
                    return response()->json([
                    'status'=>'ok',
                    'check' =>'no',
                    'message' => 'updated',                
                    ], 200);
                  }
                  else{
                    DB::commit();
                    return response()->json([
                      'status' =>'no',
                      ], 200);
                  }
              }
              else{
                  DB::commit();
                  return response()->json([
                  'status' =>'no',
                  ], 200);
              }
          }
          else{   
            return response()->json([
                'status' =>'error',
                ], 200);
          }
        }
        catch (\Exception $e) {
            DB::rollback();
            // something went wrong
            return $e;
        }    
    }

