        $created_at= date('Y-m-d h:i:s');
        $updated_at= date('Y-m-d h:i:s');
        $name=Auth::user()->name;

        DB::beginTransaction();
        try{
          $items=DB::table('otp_by_email')->where(['user_id'=>$user_id,'candidate_id'=>$candidate_id,'status'=>'0']);

          $candidate=DB::table('candidate_reinitiates')->select('name','business_id','display_id')->where('id',$candidate_id)->first();
