                          ]
                    );
            $data  = array('name'=>$name,'otp'=>$otp,'candidate_name' => $candidate_name,'reference_no'=>$reference_no,'sender'=>$sender);
            if($insert){
              Mail::send('mails.send_otp', $data, function($message) use($email) {
                $message->to($email)->subject
