    public function addressVerificationForm(Request $request)
    {
        $jaf_id = base64_decode($request->id);

        if($request->isMethod('get'))
        {
            $jaf_data=DB::table('jaf_form_data as jf')
                  ->select('jf.*','s.name as service_name','s.verification_type','s.type_name')
                  ->join('services as s','jf.service_id','=','s.id')
                  ->where(['jf.id'=>$jaf_id])
                  ->first();

            $address_verification = AddressVerification::from('address_verifications as a')
                  ->select('a.*','j.business_id')
                  ->join('jaf_form_data as j','j.id','=','a.jaf_id')
                  ->where('a.jaf_id',$jaf_id)
                  ->first();

            $verification_decision = DB::table('address_verification_decision_logs')->where('jaf_id',$jaf_id)->latest()->first();

            return view('admin.candidates.address-verification-form',compact('jaf_data','address_verification','verification_decision'));
        }

        $custom=[
          'address_type.required' => 'Select the Address Type..',
        ];

        $validator = Validator::make($request->all(), [
            'phone_number'  => 'nullable',
            'email_address'  => 'nullable|email:rfc,dns',
            'street_address'=> 'nullable',
            'house_building'=> 'nullable',
            'address_type'      => 'nullable',
            'address'           => 'nullable',
            'nature_of_residence' => 'nullable',
