            'verifier_name'         => 'nullable',
            'relation_with_verifier' => 'nullable',
            'landmark' => 'nullable',
        ],$custom);

        if ($validator->fails()){
          return response()->json([
              'success' => false,
              'errors' => $validator->errors()
          ]);
        }

        $phone_number  = $request->input('phone_number');
        $email_address  = $request->input('email_address');
        $zipcode  = $request->input('zipcode');
        $address_type  = $request->input('address_type');
        $address  = $request->input('address');
        $nature_of_residence  = $request->input('nature_of_residence');
        $period_stay_from  = $request->input('period_stay_from')!=NULL ? date('Y-m-d',strtotime($request->input('period_stay_from'))) : NULL;
        $period_stay_to  = $request->input('period_stay_to')!=NULL ? date('Y-m-d',strtotime($request->input('period_stay_to'))) : NULL;
        $verifier_name  = $request->input('verifier_name');
        $relation_with_verifier  = $request->input('relation_with_verifier');
        $landmark  = $request->input('landmark');

        DB::beginTransaction();
        try{

            $jaf_data=DB::table('jaf_form_data as jf')
                      ->select('jf.*','s.name as service_name','s.verification_type','s.type_name','u.first_name','u.last_name','u.parent_id')
                      ->join('services as s','jf.service_id','=','s.id')
                      ->join('candidate_reinitiates as u','u.id','=','jf.candidate_id')
                      ->where(['jf.id'=>$jaf_id])
                      ->first();

            $address_verification = AddressVerification::from('address_verifications as a')
                  ->select('a.*','j.business_id')
                  ->join('jaf_form_data as j','j.id','=','a.jaf_id')
                  ->where('a.jaf_id',$jaf_id)
                  ->first();
            
            if($address_verification!=NULL)
            {
                $address_data = [
                  'email'         => $email_address,
                  'phone'         => $phone_number,
                  'full_address' =>$address,
                  'nature_of_residence' =>$nature_of_residence,
                  'period_stay_from'  => $period_stay_from,
                  'period_stay_to'   => $period_stay_to,
                  'verifier_name'   => $verifier_name,
                  'relation_with_verifier' => $relation_with_verifier,
                  'landmark' => $landmark,
                  'zipcode'  =>$zipcode,
                  'updated_at'    =>date('Y-m-d H:i:s')    
                ];

                DB::table('address_verifications')
                ->where('jaf_id',$jaf_id)
                ->update($address_data);
            }
            else
            {
                $address_data = [
                  'first_name'   => $jaf_data->first_name,
                  'last_name'   => $jaf_data->last_name,
                  'business_id'  =>  $jaf_data->business_id,
                  'candidate_id'  =>$jaf_data->candidate_id,
                  'jaf_id'        => $jaf_data->id,
                  'email'         => $email_address,
                  'phone'         => $phone_number,
                  'full_address' =>$address,
                  'nature_of_residence' =>$nature_of_residence,
                  'period_stay_from'  => $period_stay_from,
                  'period_stay_to'   => $period_stay_to,
                  'verifier_name'   => $verifier_name,
                  'relation_with_verifier' => $relation_with_verifier,
                  'landmark' => $landmark,
                  'zipcode'  =>$zipcode,
                  'created_at'    =>date('Y-m-d H:i:s')    
